import './Characters.css'
import charPolice from './policeChar.jpg'
import charGirl from './girlChar.jpg'
import { useState } from 'react'
function Characters(){
    const [char1, setChar1] = useState("character1")
    const [char2, setChar2] = useState("character2")
    function setActive1(){
        if (char1 == "character1"){
            setChar1("character1 active")
        }else{
            setChar1("character1")
        }
    }
    function setActive2(){
        if (char1 == "character2"){
            setChar2("character2 active")
        }else{
            setChar2("character2")
        }
    }
    return(
    <div className="characters">
        <div className={char1}><div className='charimg1' onMouseOver={setActive1} onMouseOut={setActive1}></div><div className='char1disc'></div></div>
        <div className={char2}><div className='char2disc'></div><div className='charimg2' onMouseOver={setActive2} onMouseOut={setActive2}></div></div>
    </div>)
}
export default Characters
