import { useState } from 'react'
import './AddNew.css'
function AdminRouter(){
    const [edit,setEdit] = useState(false)
    const [addnew, setNew] = useState(false)
    const [spisok, setSpisok] = useState([])
    var url = 'http://localhost:8000'
    async function EditFunc() {
            const response = await fetch(url, {
                method: 'GET'
            });
            const data = await response.json();
            setSpisok(data.news || []);
            }
    return(
    <div className='addNew'>
        {edit && addnew?(<AdminPanelMenu/>
    ):(<div>true</div>)}
    </div>)
}

function AdminPanelMenu(){
    return(
    <div>
        <div className='HeaderPanel'>Здравствуйте, администратор</div>
        <button className='submitadmin' onClick={EditFunc}>Удалить или редактировать</button>
        <button className='submitadmin'>Добавить новость</button>
    </div>
    )
}
export default AdminRouter
